package bridgePattern;public class CryptoSystem {	private Encryption algorithm;	public CryptoSystem(String encryptionType) {		if (encryptionType.equalsIgnoreCase("Enterprise"))			algorithm = new EnterpriseEncryption();		else			algorithm = new PersonalEncryption();	}	public String encryptDoc(String string, byte key) {		return algorithm.encrypt(string, key);	}	public String decryptDoc(String string, byte key) {		return algorithm.decrypt(string, key);	}}